import React, { useState } from 'react';
import jsPDF from 'jspdf';
import axios from 'axios';

const ResumeForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    address: '',
    education: '',
    experience: '',
    template: 'template1' // Default template
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/api/generate-resume', formData);
      generatePDF(formData);
    } catch (error) {
      console.error('Error generating resume:', error);
      generatePDF(formData); // Fallback to local generation if API fails
    }
  };

  const generatePDF = (resumeData) => {
    const doc = new jsPDF();

    if (resumeData.template === 'template1') {
      // Template 1: Simple and Clean
      doc.setFontSize(20);
      doc.text('Resume', 105, 20, null, null, 'center');
      doc.setFontSize(12);
      doc.text(`Name: ${resumeData.name}`, 10, 40);
      doc.text(`Email: ${resumeData.email}`, 10, 50);
      doc.text(`Phone: ${resumeData.phone}`, 10, 60);
      doc.text(`Address: ${resumeData.address}`, 10, 70);
      doc.text(`Education: ${resumeData.education}`, 10, 80);
      doc.text(`Experience: ${resumeData.experience}`, 10, 90);
    } else if (resumeData.template === 'template2') {
      // Template 2: Modern and Stylish
      doc.setFontSize(22);
      doc.setTextColor(0, 0, 128);
      doc.text('Curriculum Vitae', 105, 20, null, null, 'center');
      doc.setFontSize(14);
      doc.setTextColor(0, 0, 0);
      doc.text(`Name: ${resumeData.name}`, 20, 40);
      doc.text(`Email: ${resumeData.email}`, 20, 50);
      doc.text(`Phone: ${resumeData.phone}`, 20, 60);
      doc.text(`Address: ${resumeData.address}`, 20, 70);
      doc.text(`Education: ${resumeData.education}`, 20, 80);
      doc.text(`Experience: ${resumeData.experience}`, 20, 90);
      // Adding a decorative line for styling
      doc.setDrawColor(0, 0, 128);
      doc.setLineWidth(0.5);
      doc.line(20, 35, 190, 35); // Horizontal line
    }

    // Save the PDF
    doc.save('resume.pdf');
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" name="name" placeholder="Name" onChange={handleChange} required />
      <input type="email" name="email" placeholder="Email" onChange={handleChange} required />
      <input type="tel" name="phone" placeholder="Phone" onChange={handleChange} required />
      <input type="text" name="address" placeholder="Address" onChange={handleChange} required />
      <textarea name="education" placeholder="Education" onChange={handleChange} required></textarea>
      <textarea name="experience" placeholder="Experience" onChange={handleChange} required></textarea>
      <select name="template" onChange={handleChange} required>
        <option value="template1">Template 1</option>
        <option value="template2">Template 2</option>
        {/* Add more templates as needed */}
      </select>
      <button type="submit">Generate Resume</button>
    </form>
  );
};

export default ResumeForm;
