import React, { useState } from 'react';
import jsPDF from 'jspdf';
import axios from 'axios';

const ResumeForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    address: '',
    education: '',
    experience: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Assuming there's an API endpoint to generate a resume.
      // If not, you can directly call generatePDF function here.
      const response = await axios.post('/api/generate-resume', formData);
      generatePDF(formData);
    } catch (error) {
      console.error('Error generating resume:', error);
      generatePDF(formData); // Fallback to local generation if API fails
    }
  };

  const generatePDF = (resumeData) => {
    const doc = new jsPDF();
    doc.text(`Name: ${resumeData.name}`, 10, 10);
    doc.text(`Email: ${resumeData.email}`, 10, 20);
    doc.text(`Phone: ${resumeData.phone}`, 10, 30);
    doc.text(`Address: ${resumeData.address}`, 10, 40);
    doc.text(`Education: ${resumeData.education}`, 10, 50);
    doc.text(`Experience: ${resumeData.experience}`, 10, 60);
    // Add other fields as needed

    // Save the PDF
    doc.save('resume.pdf');
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" name="name" placeholder="Name" onChange={handleChange} required />
      <input type="email" name="email" placeholder="Email" onChange={handleChange} required />
      <input type="tel" name="phone" placeholder="Phone" onChange={handleChange} required />
      <input type="text" name="address" placeholder="Address" onChange={handleChange} required />
      <textarea name="education" placeholder="Education" onChange={handleChange} required></textarea>
      <textarea name="experience" placeholder="Experience" onChange={handleChange} required></textarea>
      <button type="submit">Generate Resume</button>
    </form>
  );
};

export default ResumeForm;
